const fs = require("fs");
const filePath = process.platform === "linux" ? "/dev/stdin" : "./input";
const stdin = fs
  .readFileSync(filePath)
  .toString()
  .trim()
  .split("\n")
  .map((s) => s.trim());
const input = (() => {
  let line = 0;
  return () => stdin[line++];
})();

const N = input();

const M = parseInt(input());

const breakBtn = M ? input().split(" ").map(Number) : [];

const fineBtn = Array(10)
  .fill(null)
  .map((value, index) => index)
  .filter((value) => !breakBtn.includes(value));

const solve = (arr, len) => {
  // 기저 사례
  if (arr.length === len) {
    const num = parseInt(arr.join(""));
    return num.toString().length + Math.abs(N - num);
  }

  // 재귀 호출
  let ret = Infinity;

  fineBtn.forEach((value) => {
    arr.push(value);
    ret = Math.min(ret, solve(arr, len));
    arr.pop();
  });

  return ret;
};

console.log(
  Math.min(
    Math.abs(N - 100),
    N.length - 1 ? solve([], N.length - 1) : Infinity,
    solve([], N.length),
    solve([], N.length + 1)
  )
);

