const fs = require("fs");
const filePath = process.platform === "linux" ? "/dev/stdin" : "./input";
const stdin = fs
  .readFileSync(filePath)
  .toString()
  .trim()
  .split("\n")
  .map((s) => s.trim());
const input = (() => {
  let line = 0;
  return () => stdin[line++];
})();

const n = parseInt(input());

const arr = [];

for (let i = 0; i < n; i++) {
  const [x, y] = input().split(" ").map(Number);

  arr.push({ value: x, type: 0 }); // start
  arr.push({ value: y, type: 1 }); // end
}

arr.sort((a, b) => {
  if (a.value < b.value) {
    return -1;
  } else if (a.value > b.value) {
    return 1;
  } else {
    if (a.type < b.type) {
      return -1;
    } else {
      return 1;
    }
  }
});

const stack = [];

let ans = 0;

arr.forEach((item) => {
  // start
  if (item.type === 0) {
    stack.push(item);
  }
  // end
  else {
    const top = stack.pop();

    if (!stack.length) {
      ans += item.value - top.value;
    }
  }
});

console.log(ans);
