"use strict";
process.stdin.resume();
process.stdin.setEncoding("utf-8");
let [inputString, currentLine] = ["", 0];
process.stdin.on("data", (inputStdin) => (inputString += inputStdin));
process.stdin.on("end", (_) => {
  inputString = inputString
    .trim()
    .split("\n")
    .map((string) => string.trim());
  main();
});
const input = () => inputString[currentLine++];

const main = () => {
  let tc = parseInt(input());

  let ans = "";

  while (tc--) {
    const [n, q] = input().split(" ").map(Number);
    const a = input().split(" ").map(Number);

    const arr = Array.from(Array(4), () => Array(n));

    for (let i = 0; i < n; i++) {
      arr[0][i] = a[i];
    }

    for (let j = 1; j <= 3; j++) {
      for (let i = 0; i < n; i++) {
        arr[j][i] = arr[j - 1][i]
          .toString()
          .split("")
          .map(Number)
          .reduce((acc, cur) => acc + cur, 0);
      }
    }

    const count = Array(n).fill(0);

    // 세그먼트 트리 풀이 찾아서 다시풀 것
    for (let i = 0; i < q; i++) {
      const query = input().split(" ").map(Number);

      if (query[0] === 1) {
        let [l, r] = query.slice(1);

        l--, r--;

        for (let i = l; i <= r; i++) {
          count[i]++;
        }
      } else {
        let x = query[1];

        x--;

        ans += arr[count[x] < 4 ? count[x] : 3][x].toString() + "\n";
      }
    }
  }

  console.log(ans);
};
