"use strict";
process.stdin.resume();
process.stdin.setEncoding("utf-8");
let [inputString, currentLine] = ["", 0];
process.stdin.on("data", (inputStdin) => (inputString += inputStdin));
process.stdin.on("end", (_) => {
  inputString = inputString
    .trim()
    .split("\n")
    .map((string) => string.trim());
  main();
});
const input = () => inputString[currentLine++];

const main = () => {
  let tc = parseInt(input());

  let ans = "";

  while (tc--) {
    const n = parseInt(input());

    const chk = Array(2 * n + 1)
      .fill(null)
      .map((value, index) => index);

    let tmp = 1;

    if (n % 2 === 0) {
      ans += "NO" + "\n";
      continue;
    }

    let list = [];

    for (let i = Math.floor((2 * n) / 2); i >= 1; i--) {
      if (chk[i]) {
        const left = chk[i - tmp];
        const right = chk[i];

        console.log(chk.join(" "));

        if (i - tmp <= 0) {
          const pLeftIndex = chk.indexOf(2 * n + 1 - right);

          chk[i] = 0;

          list.push({ a: i, b: pLeftIndex, sum: 2 * n + 1 });
        } else {
          const pLeftIndex = chk.indexOf(2 * n + 1 - tmp - left);
          const pRightIndex = chk.indexOf(2 * n + 1 + tmp - right);

          const pLeft = chk[pLeftIndex];
          const pRight = chk[pRightIndex];

          chk[i - tmp] = chk[pLeftIndex] = 0;
          chk[i] = chk[pRightIndex] = 0;
          console.log(pLeftIndex);

          list.push({ a: i - tmp, b: pLeftIndex, sum: left + pLeft });
          list.push({ a: i, b: pRightIndex, sum: right + pRight });
        }

        tmp++;
      }
    }

    list.sort((a, b) => a.sum - b.sum);
    console.log(list);

    ans += "YES" + "\n";

    list.forEach((value) => {
      ans += `${value.a} ${value.b}\n`;
    });
  }

  console.log(ans);
};
